import './App.css';
import {useState,useEffect} from 'react';
import axios from 'axios'
import { BarChart } from '@mui/x-charts/BarChart';

function App() {
  let [data,setData] = useState(null)
  useEffect(()=>{
    axios.get("http://api.open-meteo.com/v1/forecast?latitude=32.73&longitude=-97.10&current=temperature_2m,wind_speed_10m&hourly=temperature_2m,relative_humidity_2m,wind_speed_10m").then((response)=>{
      console.log(response.data)
      setData(response.data)
    })
  },[]);

  return (
    <div className="App">
      <header className="App-header">
      <h1>Current Temperature {data?data.current.temperature_2m:''} {data?data.current_units.temperature_2m:''}</h1>
      <h1>Current Wind Speed {data?data.current.wind_speed_10m:''}  {data?data.current_units.wind_speed_10m:''}</h1>
      <BarChart
      series={data.hourly.wind_speed_10m}
      height={290}
      xAxis={[{ data: ['Q1', 'Q2', 'Q3', 'Q4'], scaleType: 'band' }]}
      margin={{ top: 10, bottom: 30, left: 40, right: 10 }}
    />
      </header>
    </div>
  );
}

export default App;
